mapper.select:
select id as managerID,quote as managerQuote,name as managerName,pictureURL as managerPicture,locked as managerStatus from user inner join userrole on user.id=userrole.userID where userrole.roleID=1
and (name=#{condition} or email=#{condition} or id=#{condition})
order by timestamp ASC limit #{start},#{limit}
